mapper.select:
SELECT

FROM TB_TODO
WHERE todo_no = #{todoNo}
  AND member_no = #{memberNo}

mapper.update:
UPDATE TB_TODO
SET
    todo_title = #{todoTitle},
    todo_content = #{todoContent},
    completed = #{completed},
    update_dtm = #{updateDtm}
WHERE todo_no = #{todoNo}
AND member_no = #{memberNo}
